sources.templates/header/header.scss:
.header-page {
    font-family: $NowieVremena;
    &__container {
        padding: 15px 40px 0px 40px;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }

    &__start {
        opacity: 0;
        &:hover {
            color: $accentColor;
        }
        &._active {
            transform: translate(0px, 0px);
            opacity: 1;
            transition: all 0.9s ease 0.4s;
        }
    }

    &__end {

    }

    &__nav {

    }

    &__ul {
        display: flex;
        justify-content: space-between;
        align-items: center;
        opacity: 0;
        &._active {
            transform: translate(0px, 0px);
            opacity: 1;
            transition: all 1s ease 0.4s;
        }
    }

    &__li {
        font-size: 18px;
        margin-right: 35px;
        transition: $transition;
        cursor: pointer;
        &:last-child {
            margin-right: 0px;
        }
        &:hover {
            color: $accentColor;
        }
    }

    &__logo {
        position: relative;
        font-family: $slab-bold;
        font-size: 20px;
        cursor: pointer;
        text-transform: uppercase;
    }
}

sources.templates/section-top/section-top.scss:
.section-top {
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    position: relative;
    padding: 100px 0 45px;

    &__container {
        position: relative;
        width: 520px;
        margin: 0;
    }

    &__title {
        font-family: $slab-bold;
        text-transform: uppercase;
        font-size: 44px;
        color: $accentColor;
        cursor: pointer;
        opacity: 0;
        transform: translate(0px, -30px);
        &._active {
            transform: translate(0px, 0px);
            opacity: 1;
            transition: all 0.8s ease 0.4s;
        }
    }

    &__info {
        color: $accentColor;
        font-size: 18px;
        transition: $transition;
        cursor: pointer;
        opacity: 0;
        transform: translate(0px, -20px);
        &._active {
            transform: translate(0px, 0px);
            opacity: 1;
            transition: all 1.3s ease 0.4s;
        }
        &:hover {
            color: black;
        }
    }
}

sources.templates/banner/banner.scss:
.banner {
        opacity: 0;
        transform: translate(0px, 40px);
        &._active {
            transform: translate(0px, 0px);
            opacity: 1;
            transition: all 1.1s ease 0.4s;
        }
    &-container {
        display: flex;
        justify-content: space-between;
        border-radius: 8px;
        background-color: #833cfcc7;
        background-position: center center;
        border-color: transparent;
        border-style: solid;
        min-height: 325px;
        position: relative;
        align-items: center;
    }

    &-left {
        &__info {
            height: 65px;
            font-family: $slab-bold;
            border: 1px solid black;
            border-radius: 8px;
            background-color: $accentColor;
            text-transform: uppercase;
            color: black;
            font-size: 48px;
            &.--first {
                position: absolute;
                transform: rotate(355deg);
                top: 10px;
                left: 30px;
            }
            &.--two {
                position: absolute;
                top: 85px;
                left: 412px;
            }
            &.--third {
                position: absolute;
                transform: rotate(3deg);
                top: 163px;
                left: -35px;
            }
            &.--fourth {
                position: absolute;
                transform: rotate(358deg);
                top: 258px;
                left: 55px;
            }
        }
    }

    &-right {
        &__text {
            margin-right: 145px;
            text-align: center;
            color: white;
            font-size: 20px;
        }
    }

    &-gif {
        position: absolute;
        transform: rotate(90deg);
        right: 95px;
        top: 80px;
        max-width: 300px;
    }

}
